import React from 'react'
import { Grid, Form, Segment, Button, Header, Message} from 'semantic-ui-react'
import {Link} from 'react-router-dom'
import firebase from '../../firebase'



class Login extends React.Component {
    state = {
        email: '',
        password: '',
        errors: [],
        loading: false

    }

    handleChange = event =>{
        this.setState({[event.target.name]: event.target.value})
    }

    handleSubmit = event =>{
        event.preventDefault();
        firebase
        .auth()
        .signInWithEmailAndPassword(this.state.email, this.state.password)
        .then(signinUser =>{
            console.log('Sign in')
        })

    }
    render() {
        const {email, password} = this.state;
        return (
            <Grid textAlign="center" verticalAlign="middle" className="app">
                <Grid.Column style={{maxWidth: 450}}>
                    <Header as="h2" icon color="orange" textAlign="center">
                        Login
                    </Header>
                    <Form size="large" onSubmit={this.handleSubmit}>
                        <Segment stacked>
                            <Form.Input fluid name="email" icon="mail" iconPosition="left"
                            placeholder="Email address" type="text" onChange={this.handleChange} value={email} />
                            <Form.Input fluid name="password" icon="key" iconPosition="left"
                            placeholder="Password" type="password" onChange={this.handleChange} value={password} />
                            <Button fluid size="large" color="orange">Login</Button>
                            <Message>
                                You don't have an account ? <Link to="/register"> Register</Link>
                            </Message>
                        </Segment>

                    </Form>
                </Grid.Column>
            </Grid>
        );
    }
}

export default Login;
