import React from 'react';
import {Grid, Form, Segment, Button, Header, Message, Icon} from 'semantic-ui-react'
import {Link} from 'react-router-dom'
import firebase from '../../firebase'
class Register extends React.Component{
    state = {
        username: '',
        email: '',
        password: '',
        passwordConfirmation: '',
    };


    handleChange = event =>{
        this.setState({[event.target.name] : event.target.value });
    };


    handleSubmit = event =>{
        this.preventDefault();

        firebase
            .auth()
            .createUserWithEmailAndPassword(this.state.email, this.state.password)
            .then(createedUser =>{
                console.log(createedUser);

            })

    }


    render(){
        const {username, email, password, passwordConfirmation} = this.state;
       return(
           <Grid textAlign="center" verticalAlign="middle" className="app">
               <Grid.Column style={{maxWidth: 450 }} >
                    <Header as="h2" icon color ="orange" textAlign="center">
                            <Icon name ="puzzle piece" color="orange" />
                            Register for ..
                    </Header>
                    <Form size="large" onSubmit={this.handleSubmit}>
                            <Segment stacked>
                                <Form.Input fluid name="username" icon="user" iconPosition="left" 
                                placeholder="Username" type="text" onChange={this.handleChange} value="username" />

                                <Form.Input fluid name="email" icon="mail" iconPosition="left"
                                placeholder="Email Address" type="text" onChange={this.handleChange} value="email" />

                                <Form.Input fluid name="password" icon="lock" iconPosition="left" 
                                placeholder="Password" type="password" onChange={this.handleChange} value="password" />

                                <Form.Input name="passwordConfirmation" icon="repeat" iconPosition="left"
                                placeholder="Password Confirmation" type="password" onChange={this.handleChange} value="passwordConfirmation"  />

                                <Button color="orange" fluid size="large">Submit</Button>
                                <Message>
                                    Already have an account ? <Link to="/Login">Login</Link>
                                </Message>
                            </Segment>
                    </Form>
               </Grid.Column>
           </Grid>
       );
    }
}

export default Register;
